import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';

function Login({ onLogin }) {
  const [isLogin, setIsLogin] = useState(true);
  const [credentials, setCredentials] = useState({ name: '', email: '', password: '' });
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

  const handleLogin = (e) => {
    e.preventDefault();
    if (credentials.email === 'admin@example.com' && credentials.password === 'admin123') {
      localStorage.setItem('user', credentials.email);
      onLogin();
      navigate('/');
    } else {
      setError('Invalid email or password');
    }
  };

  const handleSignUp = (e) => {
    e.preventDefault();
    if (credentials.name && credentials.email && credentials.password) {
      localStorage.setItem('user', credentials.email);
      onLogin();
      navigate('/');
    } else {
      setError('Please fill all fields');
    }
  };

  return (
    <div className="login">
      <div className="login-card">
        <div className="logo-text">
          <h2>Budget Planner</h2>
        </div>

        <div className="tabs">
          <button 
            className={isLogin ? 'active' : ''} 
            onClick={() => { setIsLogin(true); setError(''); }}
          >
            Login
          </button>
          <button 
            className={!isLogin ? 'active' : ''} 
            onClick={() => { setIsLogin(false); setError(''); }}
          >
            Sign Up
          </button>
        </div>

        {isLogin ? (
          <form onSubmit={handleLogin}>
            <div className="form-group">
              <label>Email:</label>
              <input type="email" name="email" value={credentials.email} onChange={handleInputChange} required />
            </div>
            <div className="form-group">
              <label>Password:</label>
              <input type="password" name="password" value={credentials.password} onChange={handleInputChange} required />
            </div>
            <button type="submit" className="btn-primary">Login</button>
          </form>
        ) : (
          <form onSubmit={handleSignUp}>
            <div className="form-group">
              <label>Name:</label>
              <input type="text" name="name" value={credentials.name} onChange={handleInputChange} required />
            </div>
            <div className="form-group">
              <label>Email:</label>
              <input type="email" name="email" value={credentials.email} onChange={handleInputChange} required />
            </div>
            <div className="form-group">
              <label>Password:</label>
              <input type="password" name="password" value={credentials.password} onChange={handleInputChange} required />
            </div>
            <button type="submit" className="btn-primary">Sign Up</button>
          </form>
        )}

        {error && <p className="error">{error}</p>}
      </div>
    </div>
  );
}

export default Login;
