import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Login from "./Login";
import Home from "./Home";

function App() {
  const [user, setUser] = useState(localStorage.getItem("user") || null);

  const handleLogin = (email) => setUser(email);
  const handleLogout = () => {
    localStorage.removeItem("user");
    setUser(null);
  };

  return (
    <Router>
      <div>
        {user && (
          <div style={{ display: "flex", justifyContent: "flex-end", padding: "10px 20px", background: "#2a9d8f", color: "white" }}>
            <span style={{ marginRight: "20px" }}>{user}</span>
            <button onClick={handleLogout} style={{ padding: "5px 10px", borderRadius: "5px", cursor: "pointer" }}>Logout</button>
          </div>
        )}

        <Routes>
          {!user ? (
            <Route path="*" element={<Login onLogin={handleLogin} />} />
          ) : (
            <Route path="/" element={<Home user={user} />} />
          )}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
