import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Dashboard from './components/Dashboard';
import Users from './components/Users';
import Categories from './components/Categories';
import Transactions from './components/Transactions';
import Login from './components/Login';
import Navbar from './components/Navbar';
import './App.css';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Optional: check if user info is in localStorage to persist login
  useEffect(() => {
    const user = localStorage.getItem('user');
    if (user) setIsLoggedIn(true);
  }, []);

  return (
    <Router>
      <div className="App">
        {isLoggedIn && <Navbar />} {/* Show navbar only if logged in */}
        <main>
          <Routes>
            {/* Public route */}
            <Route 
              path="/login" 
              element={<Login onLogin={() => setIsLoggedIn(true)} />} 
            />

            {/* Private routes */}
            <Route 
              path="/" 
              element={isLoggedIn ? <Dashboard /> : <Navigate to="/login" />} 
            />
            <Route 
              path="/users" 
              element={isLoggedIn ? <Users /> : <Navigate to="/login" />} 
            />
            <Route 
              path="/categories" 
              element={isLoggedIn ? <Categories /> : <Navigate to="/login" />} 
            />
            <Route 
              path="/transactions" 
              element={isLoggedIn ? <Transactions /> : <Navigate to="/login" />} 
            />
            
            {/* Catch-all: redirect to login if route not found */}
            <Route path="*" element={<Navigate to="/login" />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
